</template>

<script>

export default {
    data() {
        return {
            screen: {
                screenX: 0,
                screenY : 0
            }
        }
    },
    mounted() {
        this.initCanvas()
        // window.addEventListener('scroll', this.mouseInit, true)
    },
    methods: {
        initColor() {
            const deep1 = '#000'
            const deep2 = '#262626'
            const deep3 = '#565656'
            const deep4 = '#CECECE'
            const deep5 = '#FFFFFF'
            let firstLineNum = 42
            let colorArr = new Array()
            for(let i=0;i< firstLineNum; i++) {
                colorArr[i] = new Array(i)
                for(let j=0;j < firstLineNum-i;j++) {
                    colorArr[i][j] = deep1
                }
            }
            return colorArr
        },
        initCanvas() {
            let canvas = document.querySelector('#geneCanvas')
            let ctx = canvas.getContext('2d')
            ctx.lineWidth = 3
            let beginX = 10
            let beginY = 14
            let halfWidth = 6
            let firstLineNum = 42
            const colorArr = this.initColor()
            console.log(colorArr[1][1]);
            for(let i= 0; i < firstLineNum; i++) {
                // 21列
                for(let j=0; j < firstLineNum -i;j++) {
                    // 每行的格子数
                    ctx.strokeStyle = '#fff'
                    ctx.fillStyle = colorArr[i][j]
                    ctx.beginPath()
                    ctx.moveTo(beginX, beginY)
                    beginX += halfWidth
                    beginY -= halfWidth
                    ctx.lineTo(beginX, beginY)
                    beginX += halfWidth
                    beginY += halfWidth
                    ctx.lineTo(beginX, beginY)
                    beginX -= halfWidth
                    beginY += halfWidth
                    ctx.lineTo(beginX, beginY)
                    ctx.closePath()
                    ctx.stroke()
                    ctx.fill()
                    // 初始化 beginX beginY
                    beginX += halfWidth + 2.2
                    beginY -= halfWidth
                }
                beginX = 10 + halfWidth*(i+1) + 1*i 
                beginY = 14 + halfWidth*(i+1)  + 1*i
            }
        },
        mouseInit(e) {
           this.screen.screenX = e.screenX
           this.screen.screenY = e.screenY
           console.log(this.screen);
        },
    }
}
</script>

